import React, { useState } from 'react';
import { ArrowRight, ArrowLeft, CheckCircle } from 'lucide-react';
import { submitQuestionnaire } from '../services/api';

const Questionnaire = ({ onComplete }) => {
  const [questionIndex, setQuestionIndex] = useState(0);
  const [formData, setFormData] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  // UPDATED: The questionnaire is now shorter as requested.
  const questions = [
    { id: 'status', question: 'To start, what is your current status?', type: 'select', options: [
        { value: 'school_student', label: 'School Student' }, { value: 'college_student', label: 'College Student' }, { value: 'passout', label: 'Professional/Passout' }
    ]},
    { id: 'name', question: 'Great. What is your name?', type: 'text', placeholder: 'e.g., Alex Doe' },
    { id: 'age', question: 'And what is your age?', type: 'number', placeholder: 'e.g., 16' },
  ];

  const currentQuestion = questions[questionIndex];
  const isLastQuestion = questionIndex === questions.length - 1;
  const currentValue = formData[currentQuestion?.id];

  const handleOptionSelect = (value) => {
    setError('');
    setFormData({ ...formData, [currentQuestion.id]: value });
  };

  const handleNext = () => {
    // Basic validation before proceeding
    if (!currentValue) {
        setError('Please provide an answer.');
        return;
    }
    if (currentQuestion.id === 'name' && currentValue.length < 2) {
        setError('Name must be at least 2 characters.');
        return;
    }
     if (currentQuestion.id === 'age' && (parseInt(currentValue) < 10 || parseInt(currentValue) > 100)) {
        setError('Please enter a valid age.');
        return;
    }
    
    if (questionIndex < questions.length - 1) {
      setQuestionIndex(questionIndex + 1);
    } else {
      handleSubmit();
    }
  };

  const handleBack = () => {
    setError('');
    if (questionIndex > 0) {
      setQuestionIndex(questionIndex - 1);
    }
  };

  const handleSubmit = async () => {
    setIsSubmitting(true);
    setError('');
    try {
      const response = await submitQuestionnaire(formData);
      if (response.success) {
        onComplete(response);
      } else {
        const errorMessages = Object.values(response.errors || {'general': ['An unknown error occurred.']}).join('\n');
        setError(errorMessages);
      }
    } catch (error) {
      setError('An error occurred. Please ensure your backend server is running and accessible.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md">
        <div className="mb-8">
          <div className="flex justify-between items-center mb-2">
            <span className="text-sm font-medium text-gray-500">Step {questionIndex + 1} of {questions.length}</span>
            <span className="text-sm font-bold text-indigo-600">{Math.round(((questionIndex + 1) / questions.length) * 100)}%</span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-2.5"><div className="bg-indigo-600 h-2.5 rounded-full" style={{ width: `${((questionIndex + 1) / questions.length) * 100}%` }}></div></div>
        </div>
        <h2 className="text-2xl font-bold text-gray-800 mb-6">{currentQuestion.question}</h2>
        <div className="space-y-3 mb-8 min-h-[100px]">
          {currentQuestion.type === 'select' && currentQuestion.options.map(option => <button key={option.value} onClick={() => handleOptionSelect(option.value)} className={`w-full p-4 text-left rounded-xl border-2 transition-all ${currentValue === option.value ? 'border-indigo-500 bg-indigo-50' : 'border-gray-200 hover:border-indigo-300'}`}><div className="flex items-center justify-between"><span className="font-semibold">{option.label}</span>{currentValue === option.value && <CheckCircle className="w-6 h-6 text-indigo-500" />}</div></button>)}
          {currentQuestion.type === 'text' && <input type="text" placeholder={currentQuestion.placeholder} value={currentValue || ''} onChange={(e) => handleOptionSelect(e.target.value)} className="w-full p-4 border-2 border-gray-200 rounded-xl focus:border-indigo-500" />}
          {currentQuestion.type === 'number' && <input type="number" placeholder={currentQuestion.placeholder} value={currentValue || ''} onChange={(e) => handleOptionSelect(e.target.value)} className="w-full p-4 border-2 border-gray-200 rounded-xl focus:border-indigo-500" />}
        </div>
        {error && <div className="bg-red-100 text-red-700 p-3 mb-6 rounded-lg"><p>{error}</p></div>}
        <div className="flex justify-between items-center">
          <button onClick={handleBack} disabled={questionIndex === 0 || isSubmitting} className="flex items-center px-6 py-3 text-gray-600 font-semibold disabled:opacity-50"><ArrowLeft className="w-5 h-5 mr-2" />Back</button>
          <button onClick={handleNext} disabled={isSubmitting} className="flex items-center px-6 py-3 bg-indigo-600 text-white font-semibold rounded-xl shadow-md disabled:opacity-50 hover:bg-indigo-700">
            {isSubmitting ? 'Submitting...' : (isLastQuestion ? 'Start Session' : 'Next')}
            {!isSubmitting && <ArrowRight className="w-5 h-5 ml-2" />}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Questionnaire;
