import React, { useState, useEffect, useRef } from 'react';
import { getChatHistory, sendMessage } from 'services/api';
import { Menu, User, Send, Mic } from 'lucide-react';

const ChatInterface = ({ sessionData }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const sessionId = sessionData?.session_id;

  // Load chat history on component mount
  useEffect(() => {
    const loadChatHistory = async () => {
      if (!sessionId) {
        setIsLoading(false);
        setMessages([{ message_id: 'error', message: 'Error: No valid session ID found.', sender: 'ai' }]);
        return;
      }
      try {
        const response = await getChatHistory(sessionId);
        if (response && response.messages) {
          setMessages(response.messages);
        }
      } catch (error) {
        console.error('Error fetching chat history:', error);
        setMessages([{ message_id: 'error_fetch', message: 'Failed to load message history.', sender: 'ai' }]);
      } finally {
        setIsLoading(false);
        setTimeout(() => inputRef.current?.focus(), 100);
      }
    };
    loadChatHistory();
  }, [sessionId]);

  // Auto-scroll logic
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isTyping]);

  const handleSendMessage = async () => {
    if (!inputMessage.trim() || !sessionId || isTyping) return;

    const userMessage = { 
      message_id: `user_${Date.now()}`,
      message: inputMessage,
      sender: 'user',
      timestamp: new Date().toISOString()
    };
    
    setMessages(prev => [...prev, userMessage]);
    const currentInput = inputMessage;
    setInputMessage('');
    setIsTyping(true);

    try {
      const response = await sendMessage(currentInput, sessionId);
      if (response.success && response.ai_response) {
        setMessages(prev => [...prev, response.ai_response]);
        const utterance = new SpeechSynthesisUtterance(response.ai_response.message);
        speechSynthesis.speak(utterance);
      } else {
        throw new Error("Invalid AI response from backend.");
      }
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = { message_id: `err_${Date.now()}`, message: "I'm sorry, an error occurred. Please try again.", sender: 'ai', timestamp: new Date().toISOString() };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsTyping(false);
      inputRef.current?.focus();
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="h-screen w-screen bg-cover bg-center text-white" style={{ backgroundImage: `url('/background.png')` }}>
      <div className="h-full w-full bg-black bg-opacity-30 backdrop-blur-sm flex flex-col p-4">
        
        {/* Header */}
        <header className="flex items-center justify-between flex-shrink-0 px-4">
          <div className="flex items-center gap-4">
            <button className="p-2 bg-white bg-opacity-10 rounded-full hover:bg-opacity-20 transition">
              <Menu size={20} />
            </button>
            <div className="px-4 py-2 bg-white bg-opacity-10 rounded-full">
              <span className="font-medium">Career Session</span>
            </div>
          </div>
          <div className="text-2xl font-bold">*</div>
          <button className="p-2 bg-white bg-opacity-10 rounded-full hover:bg-opacity-20 transition">
            <User size={20} />
          </button>
        </header>

        {/* Main Content */}
        <main className="flex-1 flex gap-4 mt-4 overflow-hidden">
          
          {/* Left Sidebar */}
          <aside className="w-1/4 flex flex-col gap-4">
            <div className="flex-1 bg-white bg-opacity-10 backdrop-blur-md rounded-3xl p-4">
              {/* This is the top-left empty panel from the design */}
            </div>
            <div className="h-48 bg-white bg-opacity-10 backdrop-blur-md rounded-3xl p-4 flex flex-col">
              <div className="text-center py-2 mb-2 bg-white bg-opacity-20 rounded-full font-semibold">
                Chat Log
              </div>
              <ul className="text-sm space-y-2 overflow-y-auto">
                <li>• Lorem Ipsum Dolor Sit...</li>
                <li>• Consectetur Adipiscing...</li>
                <li>• Lorem Ipsum Dolor Sit...</li>
              </ul>
            </div>
          </aside>

          {/* Chat Area */}
          <section className="flex-1 flex flex-col bg-white bg-opacity-5 backdrop-blur-md rounded-3xl p-6">
            <div className="flex-1 overflow-y-auto space-y-6 pr-2">
              {isLoading ? (
                <div className="flex justify-center items-center h-full">
                  <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white"></div>
                </div>
              ) : (
                messages.map((msg) => <MessageBubble key={msg.message_id} message={msg} />)
              )}
              {isTyping && <TypingIndicator />}
              <div ref={messagesEndRef} />
            </div>

            {/* Input Area */}
            <div className="mt-4 flex items-center gap-3 bg-white bg-opacity-20 backdrop-blur-lg rounded-full p-2">
              <input
                ref={inputRef}
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Type Your Career Question..."
                className="flex-1 bg-transparent px-4 py-2 text-white placeholder-gray-300 focus:outline-none"
                disabled={isTyping || isLoading}
              />
              <button className="p-3 bg-white bg-opacity-10 rounded-full hover:bg-opacity-20 transition">
                <Mic size={20} />
              </button>
              <button
                onClick={handleSendMessage}
                disabled={!inputMessage.trim() || isTyping || isLoading}
                className="p-3 bg-white text-black rounded-full hover:bg-gray-200 disabled:opacity-50 transition"
              >
                <Send size={20} />
              </button>
            </div>
          </section>

        </main>
      </div>
    </div>
  );
};

const MessageBubble = ({ message }) => {
  const isUser = message.sender === 'user';
  return (
    <div className={`flex w-full ${isUser ? 'justify-end' : 'justify-start'}`}>
      <div className={`max-w-xl p-4 rounded-3xl ${isUser ? 'bg-green-600 bg-opacity-50 text-white rounded-br-lg' : 'bg-white bg-opacity-20 text-white rounded-bl-lg'}`}>
        <p className="whitespace-pre-wrap leading-relaxed">{message.message}</p>
      </div>
    </div>
  );
};

const TypingIndicator = () => (
  <div className="flex w-full justify-start">
    <div className="p-4 rounded-3xl bg-white bg-opacity-20 rounded-bl-lg">
      <div className="flex items-center space-x-1.5">
        <div className="w-2.5 h-2.5 bg-gray-300 rounded-full animate-bounce"></div>
        <div className="w-2.5 h-2.5 bg-gray-300 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
        <div className="w-2.5 h-2.5 bg-gray-300 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
      </div>
    </div>
  </div>
);

export default ChatInterface;
