import React, { useState, useEffect, useRef } from 'react';
import { getChatHistory, sendMessage } from '../services/api.js';

const ChatInterface = ({ sessionData }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const sessionId = sessionData?.session_id;

  useEffect(() => {
    const loadChatHistory = async () => {
      if (!sessionId) {
        setIsLoading(false);
        setMessages([{ message_id: 'error', message: 'Error: No valid session ID found.', sender: 'ai' }]);
        return;
      }
      try {
        const response = await getChatHistory(sessionId);
        if (response && response.messages) {
          setMessages(response.messages);
        }
      } catch (error) {
        console.error('Error fetching chat history:', error);
        setMessages([{ message_id: 'error_fetch', message: 'Failed to load message history.', sender: 'ai' }]);
      } finally {
        setIsLoading(false);
        setTimeout(() => inputRef.current?.focus(), 100);
      }
    };
    loadChatHistory();
  }, [sessionId]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isTyping]);

  const handleSendMessage = async () => {
    if (!inputMessage.trim() || !sessionId || isTyping) return;

    const userMessage = {
      message_id: `user_${Date.now()}`,
      message: inputMessage,
      sender: 'user',
      timestamp: new Date().toISOString()
    };
    
    setMessages(prev => [...prev, userMessage]);
    const currentInput = inputMessage;
    setInputMessage('');
    setIsTyping(true);

    try {
      const response = await sendMessage(currentInput, sessionId);
      if (response.success && response.ai_response) {
        setMessages(prev => [...prev, response.ai_response]);
        // Speak the AI's response
        const utterance = new SpeechSynthesisUtterance(response.ai_response.message);
        speechSynthesis.speak(utterance);
      } else {
        throw new Error("Invalid AI response from backend.");
      }
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = { message_id: `err_${Date.now()}`, message: "I'm sorry, an error occurred. Please try again.", sender: 'ai', timestamp: new Date().toISOString() };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsTyping(false);
      inputRef.current?.focus();
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="h-screen bg-gray-100 flex flex-col">
      <header className="bg-white shadow-md p-4 z-10">
        <h1 className="text-2xl font-bold text-gray-800 text-center">Your AI Counseling Session</h1>
      </header>
      
      <main className="flex-1 overflow-hidden p-4 flex justify-center">
        <div className="w-full max-w-4xl h-full bg-white rounded-2xl shadow-lg flex flex-col">
          <div className="flex-1 p-6 overflow-y-auto space-y-6">
            {isLoading ? (
              <div className="flex justify-center items-center h-full"><div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div></div>
            ) : (
              messages.map((msg) => <MessageBubble key={msg.message_id} message={msg} />)
            )}
            {isTyping && <TypingIndicator />}
            <div ref={messagesEndRef} />
          </div>

          <div className="bg-gray-50 border-t p-4">
            <div className="flex items-center space-x-3">
              <input ref={inputRef} type="text" value={inputMessage} onChange={(e) => setInputMessage(e.target.value)} onKeyPress={handleKeyPress}
                placeholder="Type your message..." className="flex-1 w-full px-4 py-3 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-indigo-500" disabled={isTyping || isLoading} />
              <button onClick={handleSendMessage} disabled={!inputMessage.trim() || isTyping || isLoading}
                className="px-6 py-3 bg-indigo-600 text-white font-semibold rounded-full hover:bg-indigo-700 disabled:opacity-50 transition-all transform hover:scale-105">Send</button>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

const MessageBubble = ({ message }) => {
  const isUser = message.sender === 'user';
  return (
    <div className={`flex items-end gap-3 ${isUser ? 'justify-end' : 'justify-start'}`}>
      {!isUser && <div className="w-10 h-10 rounded-full bg-indigo-500 text-white flex items-center justify-center flex-shrink-0 font-bold">AI</div>}
      <div className={`max-w-md lg:max-w-lg px-5 py-3 rounded-2xl shadow-sm ${isUser ? 'bg-indigo-600 text-white rounded-br-none' : 'bg-gray-200 text-gray-800 rounded-bl-none'}`}>
        <p className="whitespace-pre-wrap text-sm leading-relaxed">{message.message}</p>
      </div>
    </div>
  );
};

const TypingIndicator = () => (
  <div className="flex items-end gap-3 justify-start">
    <div className="w-10 h-10 rounded-full bg-indigo-500 text-white flex items-center justify-center flex-shrink-0 font-bold">AI</div>
    <div className="bg-gray-200 rounded-2xl px-5 py-4 rounded-bl-none shadow-sm">
      <div className="flex items-center space-x-1.5">
        <div className="w-2.5 h-2.5 bg-gray-400 rounded-full animate-bounce"></div>
        <div className="w-2.5 h-2.5 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
        <div className="w-2.5 h-2.5 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
      </div>
    </div>
  </div>
);

export default ChatInterface;
