import React, { useState, useEffect, useRef } from 'react';
import { getChatHistory, sendMessage, uploadResume } from '../services/api';
import { Menu, User, Send, Mic, Paperclip } from 'lucide-react';
import counselorAvatar from '../assets/avatar.png';

const ChatInterface = ({ sessionData }) => {
  // --- NO CHANGES TO ANY OF YOUR STATE, REFS, OR LOGIC ---
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const fileInputRef = useRef(null);

  const sessionId = sessionData?.session_id;

  useEffect(() => {
    const loadChatHistory = async () => {
      if (!sessionId) {
        setIsLoading(false);
        setMessages([{ message_id: 'error', message: 'Error: No valid session ID found.', sender: 'ai' }]);
        return;
      }
      try {
        const response = await getChatHistory(sessionId);
        if (response && response.messages) {
          setMessages(response.messages);
        }
      } catch (error) {
        console.error('Error fetching chat history:', error);
        setMessages([{ message_id: 'error_fetch', message: 'Failed to load message history.', sender: 'ai' }]);
      } finally {
        setIsLoading(false);
        setTimeout(() => inputRef.current?.focus(), 100);
      }
    };
    loadChatHistory();
  }, [sessionId]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isTyping]);

  const handleSendMessage = async () => {
    if (isTyping || (!inputMessage.trim() && !selectedFile) || !sessionId) return;
    if (selectedFile) {
        const file = selectedFile;
        setInputMessage(`Uploading: ${file.name}`);
        setIsTyping(true);
        try {
            const uploadResponse = await uploadResume(file, sessionId);
            const userFileMessage = { message_id: `user_file_${Date.now()}`, message: `You sent a file: ${file.name}`, sender: 'user', timestamp: new Date().toISOString() };
            if (uploadResponse.success && uploadResponse.ai_response) {
                setMessages(prev => [...prev, userFileMessage, uploadResponse.ai_response]);
                const utterance = new SpeechSynthesisUtterance(uploadResponse.ai_response.message);
                speechSynthesis.speak(utterance);
            } else {
                throw new Error("File upload succeeded, but no AI response was received.");
            }
        } catch (error) {
            console.error('Error uploading file:', error);
            const errorMessage = { message_id: `err_${Date.now()}`, message: "Sorry, there was a problem uploading your file. Please try again.", sender: 'ai', timestamp: new Date().toISOString() };
            setMessages(prev => [...prev, { message_id: `user_file_${Date.now()}`, message: `Failed to send file: ${file.name}`, sender: 'user', timestamp: new Date().toISOString() }, errorMessage]);
        } finally {
            setSelectedFile(null);
            setInputMessage('');
            setIsTyping(false);
            inputRef.current?.focus();
        }
    } else {
        const userMessage = { message_id: `user_${Date.now()}`, message: inputMessage, sender: 'user', timestamp: new Date().toISOString() };
        setMessages(prev => [...prev, userMessage]);
        const currentInput = inputMessage;
        setInputMessage('');
        setIsTyping(true);
        try {
            const response = await sendMessage(currentInput, sessionId);
            if (response.success && response.ai_response) {
                setMessages(prev => [...prev, response.ai_response]);
                const utterance = new SpeechSynthesisUtterance(response.ai_response.message);
                speechSynthesis.speak(utterance);
            } else {
                throw new Error("Invalid AI response from backend.");
            }
        } catch (error) {
            console.error('Error sending message:', error);
            const errorMessage = { message_id: `err_${Date.now()}`, message: "I'm sorry, an error occurred. Please try again.", sender: 'ai', timestamp: new Date().toISOString() };
            setMessages(prev => [...prev, errorMessage]);
        } finally {
            setIsTyping(false);
            inputRef.current?.focus();
        }
    }
  };

  const handleKeyPress = (e) => { if (e.key === 'Enter' && !e.shiftKey) { e.preventDefault(); handleSendMessage(); } };
  const handleFileSelect = () => { fileInputRef.current.click(); };
  const handleFileChange = (event) => { const file = event.target.files[0]; if (file) { setSelectedFile(file); setInputMessage(`File selected: ${file.name}`); } };

  return (
    // The parent div is now transparent, AppContainer handles the background.
    <div className="h-screen w-screen text-white font-glory">
      {/* The inner div uses padding that adapts to screen size */}
      <div className="h-full w-full bg-black bg-opacity-15 backdrop-blur-light flex flex-col p-4 md:p-8">
        
        {/* Header: Adjusted padding and text size for mobile */}
        <header className="flex items-center justify-between flex-shrink-0 px-2 md:px-8">
          <div className="flex items-center gap-0">
            <button className="p-2 md:p-3 bg-black bg-opacity-100 rounded-full hover:bg-opacity-70 transition z-10">
              <Menu size={28} className="text-white" />
            </button>
            <div className="px-4 py-2 md:px-24 md:py-4 bg-white bg-opacity-10 rounded-full -ml-8 md:-ml-16">
              <span className="font-light text-xl md:text-3xl text-black ml-4">Career Session</span>
            </div>
          </div>
          <div className="text-4xl md:text-5xl font-bold text-[#64855f]/75">*</div>
          <button className="p-3 md:p-4 bg-black bg-opacity-100 rounded-full hover:bg-opacity-70 transition">
            {/* FIXED: Replaced invalid md:size prop with responsive classNames */}
            <User className="h-[26px] w-[26px] md:h-[34px] md:w-[34px]" />
          </button>
        </header>

        {/* Main Content: Stacks vertically on mobile (flex-col), horizontal on medium screens and up (md:flex-row) */}
        <main className="flex-1 flex flex-col md:flex-row gap-4 lg:gap-10 mt-4 md:mt-8 overflow-hidden">
          
          {/* Left Sidebar: Takes full width on mobile, then shrinks on larger screens. */}
          <aside className="w-full md:w-[300px] lg:w-[400px] flex flex-col md:ml-5 h-auto md:h-full relative transition backdrop-blur-sm">
            {/* Removed the complex clipPath for better responsiveness */}
            <div className="h-full bg-white bg-opacity-10 backdrop-blur-md p-4 flex flex-col rounded-3xl">
              <div className="flex-1 mb-4">
                <div className="flex flex-col items-center text-center">
                  <img src={counselorAvatar} alt="Counselor Marvin" className="w-32 h-32 md:w-48 md:h-48 mt-4 md:mt-10 mb-4 md:mb-12 opacity-90 rounded-full"/>
                  <h3 className="mt-2 md:mt-4 text-xl md:text-2xl font-glory font-light text-white">Marvin</h3>
                  <p className="text-base md:text-lg text-white/80">Career Counselor</p>
                </div>
              </div>
              <div className="h-48">
                <div className="outline-black mt-0 text-center py-2 md:py-3 mb-2 md:mb-4 bg-white bg-opacity-100 text-black rounded-full font-light text-base md:text-xl">
                  Chat Log
                </div>
                <div className="bg-white bg-opacity-0 text-black backdrop-blur-sm rounded-3xl p-2 md:p-4 max-h-32 md:max-h-44 overflow-hidden outline-white">
                  <ul className="text-sm space-y-2 overflow-y-auto pr-2 custom-scrollbar">
                    {messages.map(msg => (
                    <li key={msg.message_id} className="truncate text-white opacity-90 font-light text-base md:text-lg">
                    â€¢ {msg.message}
                    </li>
                  ))}
                  </ul>
                </div>
              </div>
            </div>
          </aside>

          {/* Chat Area: Takes full width on mobile, flex-1 on larger screens */}
          <section className="font-roboto-flex flex-1 flex flex-col bg-white bg-opacity-5 backdrop-blur-lg rounded-3xl p-4 md:p-6 md:mr-5 shadow-2xl shadow-black/40">
            <div className="flex-1 overflow-y-auto space-y-4 md:space-y-6 pr-2 custom-scrollbar">
              {isLoading ? (
                <div className="flex justify-center items-center h-full"><div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white"></div></div>
              ) : (
                messages.map((msg) => <MessageBubble key={msg.message_id} message={msg} />)
              )}
              {isTyping && <TypingIndicator />}
              <div ref={messagesEndRef} />
            </div>

            <div className="mt-4 md:mt-6 flex items-center gap-2 md:gap-4">
              <div className="input-glow-line flex-1 flex items-center gap-2 md:gap-3 bg-white text-black text-lg backdrop-blur-lg rounded-full p-1 md:p-2">
                <input ref={inputRef} type="text" value={inputMessage} onChange={(e) => setInputMessage(e.target.value)} onKeyPress={handleKeyPress} placeholder="Type Your Question..." className="flex-1 bg-transparent px-2 md:px-4 py-1 md:py-2 text-black placeholder-gray-600 focus:outline-none text-base md:text-lg" disabled={isTyping || isLoading} />
                <button onClick={handleSendMessage} disabled={!inputMessage.trim() || isTyping || isLoading} className="p-2 md:p-3 bg-white text-black rounded-full hover:bg-gray-200 disabled:opacity-50 transition"><Send size={20} /></button>
              </div>
              <input type="file" ref={fileInputRef} onChange={handleFileChange} className="hidden" accept=".pdf,.doc,.docx,.txt" />
              {/* FIXED: Replaced invalid md:size prop with responsive classNames */}
              <button onClick={handleFileSelect} className="p-3 md:p-4 bg-black bg-opacity-100 rounded-full hover:bg-opacity-80 transition text-white"><Paperclip className="w-5 h-5 md:w-6 md:h-6" /></button>
              {/* FIXED: Replaced invalid md:size prop with responsive classNames */}
              <button className="p-3 md:p-4 bg-black bg-opacity-100 rounded-full hover:bg-opacity-80 transition text-white"><Mic className="w-5 h-5 md:w-6 md:h-6" /></button>
            </div>
          </section>
        </main>
      </div>
      <style>{`
        /* --- NO CHANGES TO YOUR STYLES --- */
        .custom-scrollbar::-webkit-scrollbar { width: 6px; }
        .custom-scrollbar::-webkit-scrollbar-track { background: transparent; }
        .custom-scrollbar::-webkit-scrollbar-thumb { background: rgba(255, 255, 255, 0.2); border-radius: 3px; }
        .custom-scrollbar::-webkit-scrollbar-thumb:hover { background: rgba(255, 255, 255, 0.3); }
        .input-glow-line { position: relative; }
        .input-glow-line::before { content: ''; position: absolute; top: -1px; left: 5%; width: 90%; height: 2px; background: rgba(255, 255, 255, 0.4); filter: blur(3px); border-radius: 2px; }
      `}</style>
    </div>
  );
};

// --- NO CHANGES to MessageBubble or TypingIndicator ---
const MessageBubble = ({ message }) => {
  const isUser = message.sender === 'user';
  const userBubbleClass = 'bg-[#64855f]/75 bg-opacity-70 text-white rounded-br-lg text-lg';
  const aiBubbleClass = 'text-black rounded-bl-lg text-lg';
  return (
    <div className={`flex w-full ${isUser ? 'justify-end' : 'justify-start'}`}>
      <div className={`max-w-xl p-4 rounded-3xl backdrop-blur-xl shadow-lg relative ${isUser ? userBubbleClass : aiBubbleClass}`}><p className="whitespace-pre-wrap leading-relaxed">{message.message}</p></div>
    </div>
  );
};
const TypingIndicator = () => (
  <div className="flex w-full justify-start">
    <div className="p-4 rounded-3xl bg-white bg-opacity-20 rounded-bl-lg backdrop-blur-xl shadow-lg">
      <div className="flex items-center space-x-1.5">
        <div className="w-2.5 h-2.5 bg-gray-300 rounded-full animate-bounce"></div>
        <div className="w-2.5 h-2.5 bg-gray-300 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
        <div className="w-2.5 h-2.5 bg-gray-300 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
      </div>
    </div>
  </div>
);

export default ChatInterface;
