import React, { useState } from 'react';
import Questionnaire from './components/Questionnaire';
import LoadingScreen from './components/LoadingScreen';
import ChatInterface from './components/ChatInterface';
import ResponsiveLayout from './components/ResponsiveLayout'; 

import './index.css';

function App() {
  const [currentStage, setCurrentStage] = useState('questionnaire');
  const [sessionData, setSessionData] = useState(null);

  const handleQuestionnaireComplete = (data) => {
    console.log('Questionnaire successful. Session data:', data);
    setSessionData(data);
    setCurrentStage('loading');
  };

  const handleLoadingComplete = () => {
    console.log('Loading complete, proceeding to chat.');
    setCurrentStage('chat');
  };

    const renderCurrentStage = () => {
    switch (currentStage) {
      case 'questionnaire':
        return (
          <ResponsiveLayout bgImage="/questionnaire-bg.jpg">
            <Questionnaire onComplete={handleQuestionnaireComplete} />
          </ResponsiveLayout>
        );
      case 'loading':
        // Assuming the loading screen should also have a background
        return (
          <ResponsiveLayout bgImage="/loading-bg.jpg"> 
            <LoadingScreen onComplete={handleLoadingComplete} />
          </ResponsiveLayout>
        );
      case 'chat':
        return (
          <ResponsiveLayout bgImage="/chat-bg.jpg">
            <ChatInterface sessionData={sessionData} />
          </ResponsiveLayout>
        );
      default:
        return (
          <ResponsiveLayout bgImage="/questionnaire-bg.jpg">
            <Questionnaire onComplete={handleQuestionnaireComplete} />
          </ResponsiveLayout>
        );
    }
  };

  return (
    <div>
      {renderCurrentStage()}
    </div>
  );
}

export default App;
