import React, { useState, useEffect, useRef } from 'react';
import { getChatHistory, sendMessage } from '../services/api.js';

const ChatInterface = ({ sessionData }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const sessionId = sessionData?.session_id;

  useEffect(() => {
    const loadChatHistory = async () => {
      if (!sessionId) {
        setIsLoading(false);
        setMessages([{ message_id: 'error', message: 'Error: No valid session ID found.', sender: 'ai' }]);
        return;
      }
      try {
        const response = await getChatHistory(sessionId);
        if (response && response.messages) {
          setMessages(response.messages);
        }
      } catch (error) {
        console.error('Error fetching chat history:', error);
        setMessages([{ message_id: 'error_fetch', message: 'Failed to load message history.', sender: 'ai' }]);
      } finally {
        setIsLoading(false);
        setTimeout(() => inputRef.current?.focus(), 100);
      }
    };
    loadChatHistory();
  }, [sessionId]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isTyping]);

  const handleSendMessage = async () => {
    if (!inputMessage.trim() || !sessionId || isTyping) return;

    const userMessage = {
      message_id: `user_${Date.now()}`,
      message: inputMessage,
      sender: 'user',
      timestamp: new Date().toISOString()
    };
    
    setMessages(prev => [...prev, userMessage]);
    const currentInput = inputMessage;
    setInputMessage('');
    setIsTyping(true);

    try {
      const response = await sendMessage(currentInput, sessionId);
      if (response.success && response.ai_response) {
        setMessages(prev => [...prev, response.ai_response]);
        // Speak the AI's response
        const utterance = new SpeechSynthesisUtterance(response.ai_response.message);
        speechSynthesis.speak(utterance);
      } else {
        throw new Error("Invalid AI response from backend.");
      }
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = { message_id: `err_${Date.now()}`, message: "I'm sorry, an error occurred. Please try again.", sender: 'ai', timestamp: new Date().toISOString() };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsTyping(false);
      inputRef.current?.focus();
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="h-screen relative overflow-hidden" 
         style={{
           background: 'linear-gradient(135deg, #667eea 0%, #764ba2 50%, #667eea 100%)',
           backgroundSize: '400% 400%',
           animation: 'gradientShift 15s ease infinite'
         }}>
      
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-20 left-20 w-32 h-32 bg-white bg-opacity-10 rounded-full blur-xl animate-pulse"></div>
        <div className="absolute bottom-32 right-16 w-48 h-48 bg-green-400 bg-opacity-20 rounded-full blur-2xl animate-bounce" style={{animationDuration: '6s'}}></div>
        <div className="absolute top-1/2 left-1/4 w-24 h-24 bg-blue-300 bg-opacity-15 rounded-full blur-xl animate-ping" style={{animationDuration: '4s'}}></div>
      </div>

      {/* Header */}
      <header className="relative z-10 p-6 flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-black bg-opacity-20 backdrop-blur-sm rounded-full flex items-center justify-center">
            <div className="w-6 h-6 bg-white rounded-sm"></div>
          </div>
          <div className="bg-black bg-opacity-20 backdrop-blur-sm rounded-full px-6 py-2">
            <span className="text-white font-medium text-sm">Career Session</span>
          </div>
        </div>
        
        {/* Nature icon */}
        <div className="text-green-300 text-2xl">ðŸŒ¿</div>
        
        {/* User avatar */}
        <div className="w-12 h-12 bg-black bg-opacity-30 backdrop-blur-sm rounded-full flex items-center justify-center">
          <div className="w-8 h-8 bg-white rounded-full flex items-center justify-center">
            <span className="text-gray-800 text-sm font-bold">ðŸ‘¤</span>
          </div>
        </div>
      </header>
      
      {/* Main Chat Container */}
      <main className="relative z-10 h-[calc(100vh-100px)] px-6 pb-6">
        <div className="h-full flex space-x-4">
          
          {/* Chat History Sidebar */}
          <div className="w-80 bg-black bg-opacity-20 backdrop-blur-md rounded-3xl p-4 flex flex-col">
            <div className="bg-white bg-opacity-90 rounded-2xl px-4 py-2 mb-4 text-center">
              <span className="text-gray-800 font-medium text-sm">Chat Log</span>
            </div>
            
            <div className="flex-1 space-y-2 overflow-y-auto">
              <div className="text-white text-xs opacity-80 px-3 py-2">â€¢ Lorem Ipsum Dolor Sit...</div>
              <div className="text-white text-xs opacity-80 px-3 py-2">â€¢ Consectetur Adipiscing...</div>
              <div className="text-white text-xs opacity-80 px-3 py-2">â€¢ Lorem Ipsum Dolor Sit...</div>
              <div className="text-white text-xs opacity-80 px-3 py-2">â€¢ Consectetur Adipiscing...</div>
            </div>
          </div>
          
          {/* Main Chat Area */}
          <div className="flex-1 flex flex-col">
            
            {/* Messages Container */}
            <div className="flex-1 mb-4 relative">
              <div className="h-full overflow-y-auto pr-4 space-y-4">
                {isLoading ? (
                  <div className="flex justify-center items-center h-full">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white"></div>
                  </div>
                ) : (
                  <>
                    {messages.map((msg) => <MessageBubble key={msg.message_id} message={msg} />)}
                    {isTyping && <TypingIndicator />}
                  </>
                )}
                <div ref={messagesEndRef} />
              </div>
            </div>

            {/* Input Area */}
            <div className="relative">
              <div className="flex items-center space-x-3 bg-white bg-opacity-95 backdrop-blur-sm rounded-full p-2 shadow-lg">
                <input 
                  ref={inputRef}
                  type="text" 
                  value={inputMessage} 
                  onChange={(e) => setInputMessage(e.target.value)} 
                  onKeyPress={handleKeyPress}
                  placeholder="Type Your Career Question..." 
                  className="flex-1 px-6 py-4 bg-transparent focus:outline-none text-gray-800 placeholder-gray-500" 
                  disabled={isTyping || isLoading} 
                />
                <button 
                  onClick={handleSendMessage} 
                  disabled={!inputMessage.trim() || isTyping || isLoading}
                  className="w-12 h-12 bg-gray-800 text-white rounded-full hover:bg-gray-700 disabled:opacity-50 transition-all flex items-center justify-center">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                  </svg>
                </button>
                <button className="w-12 h-12 bg-gray-100 text-gray-600 rounded-full hover:bg-gray-200 transition-all flex items-center justify-center">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" />
                  </svg>
                </button>
              </div>
            </div>
          </div>
        </div>
      </main>

      <style jsx>{`
        @keyframes gradientShift {
          0% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
          100% { background-position: 0% 50%; }
        }
      `}</style>
    </div>
  );
};

const MessageBubble = ({ message }) => {
  const isUser = message.sender === 'user';
  
  if (isUser) {
    return (
      <div className="flex justify-end mb-4">
        <div className="max-w-md bg-green-400 bg-opacity-90 backdrop-blur-sm text-white px-6 py-4 rounded-3xl rounded-tr-lg shadow-lg">
          <p className="whitespace-pre-wrap text-sm leading-relaxed">{message.message}</p>
        </div>
      </div>
    );
  }
  
  return (
    <div className="flex justify-start mb-4">
      <div className="max-w-lg bg-white bg-opacity-95 backdrop-blur-sm text-gray-800 px-6 py-4 rounded-3xl rounded-tl-lg shadow-lg">
        <p className="whitespace-pre-wrap text-sm leading-relaxed">{message.message}</p>
      </div>
    </div>
  );
};

const TypingIndicator = () => (
  <div className="flex justify-start mb-4">
    <div className="bg-white bg-opacity-95 backdrop-blur-sm rounded-3xl rounded-tl-lg px-6 py-4 shadow-lg">
      <div className="flex items-center space-x-2">
        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
      </div>
    </div>
  </div>
);

export default ChatInterface;
