import React, { useState } from 'react';
import { ArrowRight, ArrowLeft, Check } from 'lucide-react';
import { submitQuestionnaire } from '../services/api';
import { ReactComponent as MyLogo } from '../assets/my-logo.svg'; 

const Questionnaire = ({ onComplete }) => {
  const [questionIndex, setQuestionIndex] = useState(0);
  const [formData, setFormData] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  // UPDATED: The questionnaire is now shorter as requested.
  const questions = [
    { id: 'status', question: 'To start, what is your current status?', type: 'select', options: [
        { value: 'school_student', label: 'School Student' }, { value: 'college_student', label: 'College Student' }, { value: 'passout', label: 'Professional/Passout' }
    ]},
    { id: 'name', question: 'Great. What is your name?', type: 'text', placeholder: 'e.g., Alex Doe' },
    { id: 'age', question: 'And what is your age?', type: 'number', placeholder: 'e.g., 16' },
  ];

  const currentQuestion = questions[questionIndex];
  const isLastQuestion = questionIndex === questions.length - 1;
  const currentValue = formData[currentQuestion?.id];

  const handleOptionSelect = (value) => {
    setError('');
    setFormData({ ...formData, [currentQuestion.id]: value });
  };

  const handleNext = () => {
    // Basic validation before proceeding
    if (!currentValue) {
        setError('Please provide an answer.');
        return;
    }
    if (currentQuestion.id === 'name' && currentValue.length < 2) {
        setError('Name must be at least 2 characters.');
        return;
    }
     if (currentQuestion.id === 'age' && (parseInt(currentValue) < 10 || parseInt(currentValue) > 100)) {
        setError('Please enter a valid age.');
        return;
    }
    
    if (questionIndex < questions.length - 1) {
      setQuestionIndex(questionIndex + 1);
    } else {
      handleSubmit();
    }
  };

  const handleBack = () => {
    setError('');
    if (questionIndex > 0) {
      setQuestionIndex(questionIndex - 1);
    }
  };

  const handleSubmit = async () => {
    setIsSubmitting(true);
    setError('');
    try {
      const response = await submitQuestionnaire(formData);
      if (response.success) {
        onComplete(response);
      } else {
        const errorMessages = Object.values(response.errors || {'general': ['An unknown error occurred.']}).join('\n');
        setError(errorMessages);
      }
    } catch (error) {
      setError('An error occurred. Please ensure your backend server is running and accessible.');
    } finally {
      setIsSubmitting(false);
    }
  };

return (
    <div 
        className="min-h-screen w-full bg-cover bg-center flex flex-col items-center justify-center p-4 text-white relative"
        // IMPORTANT: Replace with the correct path to your background image in the `public` folder
        style={{ backgroundImage: 'url(/questionnarie-bg.png)' }}
    >
        {/* Header with Logo */}
        <div className="absolute top-8 left-8 flex items-center gap-5">
            <MyLogo className="w-6 h-6 text-white" />
            
            <span className="text-4xl opacity-70 font-glory font-extralight">Vision Track</span>
        </div>
      
        {/* Glassmorphism Card */}
        <div className="bg-green-300/10 backdrop-blur-lg border border-white rounded-3xl shadow-2xl p-8 w-full max-w-lg flex flex-col">
            
            <h2 className="text-2xl font-light text-center mb-8 mt-8">{currentQuestion.question}</h2>

            {/* Add this line */}
            <div className="w-60 h-px bg-white opacity-50 my-4 mb-12 mx-auto"></div>
            
            <div className="space-y-4 mb-10 min-h-[150px]">
                {currentQuestion.type === 'select' && currentQuestion.options.map(option => (
                    <button 
                        key={option.value} 
                        onClick={() => handleOptionSelect(option.value)} 
                        className={`w-full p-4 text-left rounded-xl transition-all flex justify-between items-center`}
                    >
                        <span className="font-extralight text-lg font-roboto-flex opacity-90">{option.label}</span>
                        {/* Show checkmark if selected */}
                        {currentValue === option.value && <Check className="w-6 h-6 text-white" />}
                    </button>
                ))}

                {currentQuestion.type === 'text' && (
                    <input 
                        type="text" 
                        placeholder={currentQuestion.placeholder} 
                        value={currentValue || ''} 
                        onChange={(e) => handleOptionSelect(e.target.value)} 
                        className="w-full p-4 bg-transparent border-b-2 border-white/30 text-white text-xl text-center placeholder:text-white/50 focus:outline-none focus:border-white transition-colors"
                    />
                )}

                {currentQuestion.type === 'number' && (
                    <input 
                        type="number" 
                        placeholder={currentQuestion.placeholder} 
                        value={currentValue || ''} 
                        onChange={(e) => handleOptionSelect(e.target.value)} 
                        className="w-full p-4 bg-transparent border-b-2 border-white/30 text-white text-xl text-center placeholder:text-white/50 focus:outline-none focus:border-white transition-colors"
                    />
                )}
            </div>
            {/* Add this line */}
            <div className="w-60 h-px bg-white opacity-50 my-4 mx-auto"></div>
            {/* Styled Error Message */}
            {error && (
                <div className="bg-red-500/20 border border-red-500 text-white p-3 mb-6 rounded-lg text-center">
                    <p>{error}</p>
                </div>
            )}
            
            {/* New Navigation and Progress Area */}
            <div className="flex justify-between items-center mt-auto">
                <button 
                    onClick={handleBack} 
                    disabled={questionIndex === 0 || isSubmitting} 
                    className="flex items-center justify-center w-14 h-14 bg-white/10 border border-white/20 rounded-full disabled:opacity-30 hover:bg-white/20 transition-colors"
                >
                    <ArrowLeft className="w-6 h-6" />
                </button>
                
                <div className="flex flex-col items-center">
                    <div className="w-60 bg-white/20 rounded-full h-1">
                        <div className="bg-white h-1 rounded-full" style={{ width: `${((questionIndex + 1) / questions.length) * 100}%` }}></div>
                    </div>
                    <span className="text-white/80 font-light mt-2 text-sm">Question {questionIndex + 1} of {questions.length}</span>
                </div>
                
                <button 
                    onClick={handleNext} 
                    disabled={isSubmitting} 
                    className="flex items-center justify-center w-14 h-14 bg-white/10 border border-white/20 rounded-full disabled:opacity-30 hover:bg-white/20 transition-colors"
                >
                    <ArrowRight className="w-6 h-6" />
                </button>
            </div>
        </div>
    </div>
  );
};

export default Questionnaire;
