import React, { useState, useEffect, useRef } from 'react';
import { getChatHistory, sendMessage } from '../services/api.js';

const ChatInterface = ({ sessionData }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);
  const fileInputRef = useRef(null); // Reference for the hidden file input

  const sessionId = sessionData?.session_id;

  useEffect(() => {
    const loadChatHistory = async () => {
      if (!sessionId) {
        setIsLoading(false);
        setMessages([{ message_id: 'error', message: 'Error: No valid session ID found.', sender: 'ai' }]);
        return;
      }
      try {
        const response = await getChatHistory(sessionId);
        if (response && response.messages) {
          setMessages(response.messages);
        }
      } catch (error) {
        console.error('Error fetching chat history:', error);
        setMessages([{ message_id: 'error_fetch', message: 'Failed to load message history.', sender: 'ai' }]);
      } finally {
        setIsLoading(false);
        setTimeout(() => inputRef.current?.focus(), 100);
      }
    };
    loadChatHistory();
  }, [sessionId]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isTyping]);

  const handleSendMessage = async (messageContent, fileContent = null) => {
    if ((!messageContent.trim() && !fileContent) || !sessionId || isTyping) return;

    const userMessage = {
      message_id: `user_${Date.now()}`,
      message: messageContent || "File attached.",
      sender: 'user',
      timestamp: new Date().toISOString()
    };
    
    setMessages(prev => [...prev, userMessage]);
    
    // Create a temporary message for a typing indicator
    const typingIndicator = { message_id: `typing_${Date.now()}`, message: '', sender: 'ai', isTyping: true };
    setMessages(prev => [...prev, typingIndicator]);
    
    setIsTyping(true);

    try {
      const response = await sendMessage(sessionId, messageContent, fileContent);
      if (response.success && response.ai_response) {
        setMessages(prev => prev.filter(msg => !msg.isTyping)); // Remove typing indicator
        setMessages(prev => [...prev, response.ai_response]);
        // Speak the AI's response
        const utterance = new SpeechSynthesisUtterance(response.ai_response.message);
        speechSynthesis.speak(utterance);
      } else {
        throw new Error("Invalid AI response from backend.");
      }
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = { message_id: `err_${Date.now()}`, message: "I'm sorry, an error occurred. Please try again.", sender: 'ai', timestamp: new Date().toISOString() };
      setMessages(prev => prev.filter(msg => !msg.isTyping)); // Remove typing indicator
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsTyping(false);
      setInputMessage(''); // Clear the input field
      inputRef.current?.focus();
    }
  };

  const handleTextSubmit = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage(inputMessage);
    }
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        handleSendMessage('', e.target.result);
      };
      reader.readAsText(file);
    }
  };

  return (
    <div className="h-screen bg-gray-100 flex flex-col">
      <header className="bg-white shadow-md p-4 z-10">
        <h1 className="text-2xl font-bold text-gray-800 text-center">Your AI Counseling Session</h1>
      </header>
      
      <main className="flex-1 overflow-hidden p-4 flex justify-center">
        <div className="w-full max-w-4xl h-full bg-white rounded-2xl shadow-lg flex flex-col">
          <div className="flex-1 p-6 overflow-y-auto space-y-6">
            {isLoading ? (
              <div className="flex justify-center items-center h-full"><div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div></div>
            ) : (
              messages.map((msg) => msg.isTyping ? <TypingIndicator key={msg.message_id} /> : <MessageBubble key={msg.message_id} message={msg} />)
            )}
            <div ref={messagesEndRef} />
          </div>

          <div className="bg-gray-50 border-t p-4">
            <div className="flex items-center space-x-3">
              {/* Hidden file input element */}
              <input 
                type="file" 
                ref={fileInputRef} 
                onChange={handleFileChange} 
                className="hidden" 
                accept=".txt, .md, .pdf, .docx"
              />
              {/* Attachment button */}
              <button 
                onClick={() => fileInputRef.current?.click()}
                className="p-3 text-gray-500 hover:text-indigo-600 transition-colors duration-200"
                disabled={isTyping || isLoading}
              >
                 <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}>
                   <path strokeLinecap="round" strokeLinejoin="round" d="M15.172 7l-6.586 6.586a2 2 0 102.828 2.828l6.414-6.586a4 4 0 00-5.656-5.656l-6.415 6.585a6 6 0 108.486 8.486L20.5 14.5" />
                 </svg>
              </button>

              <input 
                ref={inputRef} 
                type="text" 
                value={inputMessage} 
                onChange={(e) => setInputMessage(e.target.value)} 
                onKeyPress={handleTextSubmit}
                placeholder="Type your message..." 
                className="flex-1 w-full px-4 py-3 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-indigo-500" 
                disabled={isTyping || isLoading} 
              />
              <button 
                onClick={() => handleSendMessage(inputMessage)} 
                disabled={!inputMessage.trim() || isTyping || isLoading}
                className="px-6 py-3 bg-indigo-600 text-white font-semibold rounded-full hover:bg-indigo-700 disabled:opacity-50 transition-all transform hover:scale-105">Send</button>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

const MessageBubble = ({ message }) => {
  const isUser = message.sender === 'user';
  return (
    <div className={`flex items-end gap-3 ${isUser ? 'justify-end' : 'justify-start'}`}>
      {!isUser && <div className="w-10 h-10 rounded-full bg-indigo-500 text-white flex items-center justify-center flex-shrink-0 font-bold">AI</div>}
      <div className={`max-w-md lg:max-w-lg px-5 py-3 rounded-2xl shadow-sm ${isUser ? 'bg-indigo-600 text-white rounded-br-none' : 'bg-gray-200 text-gray-800 rounded-bl-none'}`}>
        <p className="whitespace-pre-wrap text-sm leading-relaxed">{message.message}</p>
      </div>
    </div>
  );
};

const TypingIndicator = () => (
  <div className="flex items-end gap-3 justify-start">
    <div className="w-10 h-10 rounded-full bg-indigo-500 text-white flex items-center justify-center flex-shrink-0 font-bold">AI</div>
    <div className="bg-gray-200 rounded-2xl px-5 py-4 rounded-bl-none shadow-sm">
      <div className="flex items-center space-x-1.5">
        <div className="w-2.5 h-2.5 bg-gray-400 rounded-full animate-bounce"></div>
        <div className="w-2.5 h-2.5 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
        <div className="w-2.5 h-2.5 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
      </div>
    </div>
  </div>
);

export default ChatInterface;
